/// <reference types="react" />

import * as React from "react";

interface HTMLAttributesWeak extends React.HTMLAttributes<HTMLElement> {
  title?: any;
  onError?: any;
}

export interface ImgProps extends HTMLAttributesWeak {
  /**
   * 图片显示模式
   */
  type?: "cover" | "contain";

  /**
   * 图片 url
   */
  src: string;

  /**
   * 图片的 title，hover 上去会显示出来
   */
  title?: string;

  /**
   * 图片加载失败的兜底图片
   */
  errorImgSrc?: string;

  /**
   * 图片加载失败的回调方法
   */
  onError?: () => void;

  /**
   * 图片显示宽度
   */
  width?: number;

  /**
   * 图片显示高度
   */
  height?: number;

  /**
   * 图片的 alt 通常用于屏幕阅读器（盲人）识别
   */
  alt?: string;

  /**
   * 形状展现
   */
  shape?: string;

  /**
   * 启用阿里 CDN 图片优化压缩后缀
   */
  enableAliCDNSuffix?: boolean;
}

export default class Img extends React.Component<ImgProps, any> {}
